    public void creazioneMappa(Graphics g) {
        int size = 32; // o la dimensione del tuo tile (es: 64)

        for (int y = 0; y < map.length; y++) {
            for (int x = 0; x < map[0].length; x++) {
                int tileIndex = map[y][x];
                if (tileIndex >= 0 && tileIndex < img.length) {
                    g.drawImage(img[tileIndex].getImage(), x * size, y * size, 48,48, null);
                }
            }
        }
    }
